import React, { lazy } from "react";
import styled from "styled-components";

import im1 from '../../assets/1.jpg'
import im2 from '../../../src/images/2.jpg'
import im3 from '../../../src/images/3.jpg'
import im4 from '../../../src/images/4.jpg'

import Slider from "react-slick";
import "../../../node_modules/slick-carousel/slick/slick.css";
import "../../../node_modules/slick-carousel/slick/slick-theme.css";

const Card = lazy(() => import("../../components/Card/index"));

const Section = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  padding: 5rem 0;
`;

const Title = styled.h1`
  color: #0a0b10;
  display: inline-block;
  font-size: calc(1rem + 1.5vw);
  margin-top: 1.5rem;
  position: relative;
  &::before {
    content: "";
    height: 1px;
    width: 50%;
    position: absolute;
    left: 50%;
    bottom: 0;
    transform: translate(-50%, 0.5rem);
    /* or 100px */
    border-bottom: 2px solid var(--purple);
  }
`;

const Carousal = styled.div`
  width: 50vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  @media only Screen and (max-width: 40em) {
    width: 90vw;
    .slick-slider .slick-arrow {
      display: none;
    }
  }
  .slick-slider .slick-arrow:before {
    color: #0a0b10;
    font-size: 1.5rem;
    @media only Screen and (max-width: 40em) {
      display: none;
    }
  }
  .slick-slider .slick-dots button:before {
    color: #0a0b10;
    font-size: 1.5rem;
  }
  .slick-slide.slick-active {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    margin: 0;
    padding: 0;
    margin-bottom: 3rem;
  }
`;

const Testimonials = () => {
  const settings = {
    dots: true,
    infinite: true,

    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <section className='login'>
            <div className='loginContainer'>
                <h2 style={{ display: "flex", justifyContent: "center", display: 'flex', alignItems: "center" }}>Payment page</h2>
                <div style={{ alignItems: 'center', justifyItems: 'center', flexDirection: 'column' }}>
                    <div style={{ alignItems: 'center', display: 'flex', justifyContent: 'center', flexDirection: 'row' }}>
                        <img style={{ width: '40%', padding: 5, borderRadius: 10 }} src={im1} />
                        <img style={{ width: '40%', padding: 5, borderRadius: 10 }} src={im2} />
                    </div>
                    <div style={{ alignItems: 'center', display: 'flex', justifyContent: 'center', flexDirection: 'row' }}>
                        <img style={{ width: '40%', padding: 5, borderRadius: 10 }} src={im3} />
                        <img style={{ width: '40%', padding: 5, borderRadius: 10 }} src={im4} />
                    </div>
                </div>
                <div className='btnContainer'>
                    <button onClick={(e) => {
                        e.preventDefault();
                        window.location.href = 'https://rzp.io/l/TKTLU9izOv';
                    }}>Donate</button>
                </div>
            </div>
        </section>
  );
};

export default Testimonials;
